import '../App.css';
import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout'
import Data from '../components/Data'
import '../App.css';
import { useNavigate } from 'react-router-dom';
import { useDataContext } from '../DataContext';
import SummaryComponent from '../components/dashboardStatsComponents/SummaryComponent';
import SongsComponent from '../components/dashboardStatsComponents/SongsComponent';

function AuthorizedPage() {
  const { artistsData, trackData } = useDataContext();
  const [renderSwitch, setRenderSwitch] = useState(1);
  const [labelVisibility, setLabelVisibility] = useState({
    1: true,
    2: false,
    3: false,
    4: false,
    5: false,
  });

  const handleLabelClick = (label) => {
    setRenderSwitch(label);
    setLabelVisibility((prevVisibility) => ({
      ...Object.fromEntries(Object.entries(prevVisibility).map(([key]) => [key, key === label])),
    }));
  };

  var username = "Morgase";
  const navigate = useNavigate();
  function handleLogo() {
    navigate('/');
  }

  useEffect(() => {
    console.log("");
  }, []);

  return (
    <Layout>
      <Data>
        <div className='hero'>
          <div className='shadow blue'></div>

          <div className='main-auth-container'>
            <header className='landing-header'>
              <h1 onClick={handleLogo} className='logo'>
                <span className='green-text'>Spotify</span>Stats
              </h1>
              <div className='positioning-div'>
                <h2>Account</h2>
                <h2>Settings</h2>
                <div className='auth-button'>
                  <h2>My Profile</h2>
                </div>
              </div>
            </header>

            <div className='main-stats-container'>
              <div className='label-container'>
                <h1 className='username focus'>{username}</h1>
                <div className='positioning-div'>
                  {[1, 2, 3, 4, 5].map((label) => (
                    <h1
                      key={label}
                      className={`focus ${labelVisibility[label] ? '' : 'hidden'}`}
                      onClick={() => handleLabelClick(label)}
                    >
                      {getLabelName(label)}
                      <div className={`bottom-green-border ${labelVisibility[label] ? '' : 'hidden'}`}></div>
                    </h1>
                  ))}
                </div>
              </div>

              <div className='outlined-stats-container'>
                {renderSwitch === 1 ? (
                  <SummaryComponent trackData={trackData} artistsData={artistsData} />
                ) : renderSwitch === 2 ? (
                  <SongsComponent trackData={trackData} />
                ) : renderSwitch === 3 ? (
                  <SummaryComponent trackData={trackData} artistsData={artistsData} />
                ) : renderSwitch === 4 ? (
                  <SummaryComponent trackData={trackData} artistsData={artistsData} />
                ) : (
                  <SummaryComponent trackData={trackData} artistsData={artistsData} />
                )}
              </div>
            </div>
          </div>
        </div>
      </Data>
    </Layout>
  );
}

const getLabelName = (label) => {
  // You can customize the label names based on your requirements
  switch (label) {
    case 1:
      return 'Summary';
    case 2:
      return 'Songs';
    case 3:
      return 'Artists';
    case 4:
      return 'Albums';
    case 5:
      return 'Genres';
    default:
      return '';
  }
};

export default AuthorizedPage;
