import '../App.css';
import './dashboard.css'
import React, { useContext, useEffect } from 'react';
import MyContext from '../myContext';

function AuthorizedPage() {
  const queryString = window.location.search; // Gets the query string (?param1=value1&param2=value2)
  const params = new URLSearchParams(queryString);
  const access_token = params.get('access_token');
  const refresh_token = params.get('refresh_token');
  localStorage.setItem('access_token', access_token);
  localStorage.setItem('refresh_token', refresh_token)
  const limit = 10;

  function isLoggedIn() {
    fetch(`http://localhost:5000/token_valid?access_token=${access_token}`)
    .then(response => {
      if (response.data.access_token === true) {
        console.log(true)
        return true;
      }
      else {
        console.log(false)
        return false;
      }
    })
  }

  function topArtists() {
    fetch(`http://localhost:5000/top-artists?access_token=${access_token}&limit=${limit}`)
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log(data);        
    })
    .catch(error => {
      console.error('Fetch error:', error);
    });
  }

  function topTracks() {
    fetch(`http://localhost:5000/top-tracks?access_token=${access_token}&limit=${limit}`)
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log(data);        
    })
    .catch(error => {
      console.error('Fetch error:', error);
    });
  }

  function logout() {

  }

  return (
    <div onLoad={isLoggedIn}>
        <button onClick={topTracks}>Get Tracks</button>
        <br></br>
        <button onClick={topArtists}>Get Artists</button>
        {/* <div className='hero'>
          <div className='main-container'>
            <div className='artist-container'></div>
            <div className='track-container'>
              <div className='slider-container'></div>
            </div>
          </div>
        </div> */}
        <button onClick={logout}>Logout</button>
    </div>
  );
}

export default AuthorizedPage;
