  return (
    <div className='outlined-stats-container'>
      <div className='glassmorphism-test'>

      </div>

      <div className='dashboard-flexbox-container'>
        <div className='topArtists-container'>
          <h1>Top Artists</h1>
          <div className='topArtists'>
            <div className='image-carosel'>
              <img className='second-image' src={artistsData[1].image[0].url}></img>
              <img className='first-image' src={artistsData[0].image[0].url}></img>
              <img className='second-image' src={artistsData[2].image[0].url}></img>
            </div>
            {artistsData.map((artist, index) => (
              <ArtistCard
                key={index}
                imageSrc={artist.image[0].url} 
                artistName={artist.name}
                position={artist.position}
              />
            ))}
          </div>
          </div>
            <div className='topArtists-container'>
              <h1>Top Tracks</h1>
              <div className='topArtists'>
                <div className='image-carosel'>
                  <img className='second-image' src={trackData[1].image[0].url}></img>
                  <img className='first-image' src={trackDataa[0].image[0].url}></img>
                  <img className='second-image' src={trackDataData[2].image[0].url}></img>
                </div>
                {trackData.map((track, index) => (
                  <TrackCard
                    key={index}
                    imageSrc={track.image[0].url}
                    trackName={track.name}
                    artistName={track.artist || 'Unknown'}
                    position={track.position}
                  />
                ))}
              </div>
            </div>
          </div>
    
        </div>
  );
}

export default SummaryComponent;
