import '../App.css';
import './dashboard.css'
import React, { useContext, useEffect } from 'react';
import MyContext from '../myContext';
import { useNavigate } from 'react-router-dom';
import { logout } from "./utils";
import ArtistCard from './artistCard';


function AuthorizedPage() {
  const [artistsData, setArtistsData] = useState([]);

  const queryString = window.location.search; // Gets the query string (?param1=value1&param2=value2)
  const params = new URLSearchParams(queryString);
  const access_token = params.get('access_token');
  const refresh_token = params.get('refresh_token');
  localStorage.setItem('access_token', access_token);
  localStorage.setItem('refresh_token', refresh_token)
  const limit = 10;
  const navigate = useNavigate();


  async function isLoggedIn() {
    try {
      const response = await fetch(`http://localhost:5000/token_valid?access_token=${access_token}`);
      if (response.ok) {
        const data = await response.json();
        console.log(data.valid);
        return data.valid;
      } else {
        console.log(false);
        return false;
      }
    } catch (error) {
      console.error('Fetch error:', error);
      return false;
    }
  }

  function topArtists() {
    fetch(`http://localhost:5000/top-artists?access_token=${access_token}&limit=${limit}`)
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log(data);        
    })
    .then(data => setArtistsData(data))
    .catch(error => {
      console.error('Fetch error:', error);
    });
  }

  function topTracks() {
    fetch(`http://localhost:5000/top-tracks?access_token=${access_token}&limit=${limit}`)
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log(data);        
    })
    .catch(error => {
      console.error('Fetch error:', error);
    });
  }


  function handleLogout() {
    logout(navigate);
  }

  useEffect(() => {
    isLoggedIn();
  }, []);

  return (
    <div>
        <button onClick={topTracks}>Get Tracks</button>
        <br />
        <button onClick={topArtists}>Get Artists</button>
        <br />
        {/* <div className='hero'>
          <div className='main-container'>
            <div className='artist-container'></div>
            <div className='track-container'>
              <div className='slider-container'></div>
            </div>
          </div>
        </div> */}

        <button onClick={handleLogout}>Logout</button>
        <div>
          {artistsData.map((artist, index) => (
            <ArtistCard
              key={index}
              imageSrc={artist.image[0].url} // Assuming the image is an array and taking the first element
              artistName={artist.name}
              position={artist.position}
            />
          ))}
        </div>
    </div>
  );
}

export default AuthorizedPage;
