import '../App.css';
import './dashboard.css'
import React, { useContext } from 'react';
import MyContext from '../myContext';

function AuthorizedPage() {
  const queryString = window.location.search; // Gets the query string (?param1=value1&param2=value2)
  const params = new URLSearchParams(queryString);
  const code = params.get('access_token');
  localStorage.setItem('access_token', code);
  const limit = 10;


  function topArtists() {
    fetch(`http://localhost:5000/top-artists?access_token=${code}&limit=${limit}`)
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log(data);        
    })
    .catch(error => {
      console.error('Fetch error:', error);
    });
  }

  function topTracks() {
    fetch(`http://localhost:5000/top-tracks?access_token=${code}&limit=${limit}`)
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log(data);        
    })
    .catch(error => {
      console.error('Fetch error:', error);
    });
  }


  return (
    <div>
        <button onClick={topTracks}>Get Tracks</button>
        <button onClick={topArtists}>Get Artists</button>
        <div className='hero'>
          <div className='main-container'>
            <div className='artist-container'></div>
            <div className='track-container'>
              <div className='slider-container'></div>
            </div>
          </div>
        </div>
    </div>
  );
}

export default AuthorizedPage;
