export function gatherData(access_token, term) {
  const { setTrackData, trackData, setGenreData, setAlbumData, setUserData, setArtistsData} = useDataContext();

  // if (trackData[0]) {
  //   console.log("already has set data in react context")
  //   return;
  // }
  console.log(trackData)
  fetch(`http://localhost:5000/user-data?access_token=${access_token}&term=${term}`)
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      setTrackData(data.songs) 
      setArtistsData(data.artists) 
      setGenreData(data.genres) 
      setAlbumData(data.albums)
      setUserData(data.user)
    })
    .catch(error => {
      console.error('Fetch error:', error);
    });
}
